resources.string:
uniform mat4 uMVPMatrix;
uniform mat4 bonesMatrices[2];
attribute vec3 vPosition;
attribute vec3 a_TexCoordinate;
varying vec2 texCoord;

void main() {
    int boneIntIndex = int(a_TexCoordinate.z);

    vec4 position = vec4(vPosition.xyz, 1.0) * bonesMatrices[boneIntIndex];

    gl_Position = uMVPMatrix * vec4(position.xyz, 1.0);

    texCoord = a_TexCoordinate.xy;
}

precision mediump float;
uniform sampler2D u_Texture;
varying vec2 texCoord;

void main() {

vec4 color = texture2D(u_Texture, texCoord);

if(color.a<0.5){
    discard;
}

gl_FragColor = color;
}
